var timer;
msg.payload = 0;
node.send(msg);       //Send foerste msg

//Funksjonen som gjentas
function countAndRepeat() {
    msg.payload = msg.payload + 1;
    node.send(msg);    //Send oppdatert msg 
    timer = setTimeout(function(){countAndRepeat();}, 1000); //Gjenta om 1s
}

//Foelgende skjer bare ved ny 'deploy'
node.on('close',function(){
		clearTimeout(timer);
		node.warn('timer stopped');
});

//Starter funksjonen som gjentar seg om 1000ms
timer = setTimeout(function(){countAndRepeat();}, 1000);
