var SerialPort = global.get("SerialPort");
var mavlink = global.get("mavlink");

var connection = {};
connection.devicepath = null;
connection.mavReady = false;
connection.tout = null;         //timer the restarts connection if (unplanned) closed
connection.shutdown = false;
connection.lasterror = "";

var debug = true;

var settings = {};
settings.serialReconnectTime = 3000;

//------------------------------------------
//      FUNCTIONS
//------------------------------------------

function findPathToManufacturer(founddevices, manufacturer, manufacturerTELE) {
    var devices = founddevices;
    //console.log(JSON.stringify(devices));
    for (var i=0; i<devices.length; i++) {
        //console.log(ports[i].manufacturer);
        if (devices[i].manufacturer == manufacturer) {
            //console.log(devices[i].path);
            return devices[i].path;
        } else if (devices[i].manufacturer == manufacturerTELE) {
            //console.log(devices[i].path);
            return devices[i].path;
        }
    }
    return null;
}

connection.writemessage = function(message) {
    connection.port.write(message.buffer);
    //node.warn(message.buffer);
};

function tryToOpenPort(founddevicepath) {
    if (!connection.shutdown) {
        if (founddevicepath !== null) {
            node.status({fill:"green",shape:"ring",text:'Pixhawk/telemetry-device found at port '+founddevicepath});
            try {
                //Open serial port
                connection.port = new SerialPort(founddevicepath, {
                    //baudRate: 921600
                    baudRate: 57600
                    //baudRate: 115200 
                }, function(err, results) {
                    if (err) {
                        node.status({fill:"red",shape:"dot",text:'Serial Port could not be created'});
                        let currerror = err.toString();
                        if (currerror != connection.lasterror) {
                            connection.lasterror = err.toString();
                            console.log(connection.lasterror);
                        }
                        if (connection.tout !== null) { clearTimeout(connection.tout); }
                        connection.tout = setTimeout(tryToOpenPort(null), settings.serialReconnectTime);
                    }
                });
                node.status({fill:"green",shape:"dot",text:'Serial Port is ready'});

                connection.port.on('open', function() {
                    console.log("Pixhawk/telemetry-device connected!");
            	    node.status({fill:"green",shape:"dot",text:'Serial Port is open'});
            	    if (connection.tout !== null) { clearTimeout(connection.tout); }
            	    if (debug) {
            	        flow.set("test",[connection]);
            	    }
            		setTimeout(node.status({fill:"green",shape:"dot",text:'connected'}), 2000);
                });

                //Error handling
                connection.port.on('error', function () {
                    if (connection.port.isOpen) {
                        node.status({fill:"yellow",shape:"ring",text:"An error occured..."});
                        connection.port.close(function (err) {
                            node.log('Pixhawk/telemetry-port closed with error');
                            node.log(err);
                        //console.log('port closed', err);
                        });
                    } 
                    if (connection.tout !== null) { clearTimeout(connection.tout); }
            		connection.tout = setTimeout(tryToOpenPort(null), settings.serialReconnectTime);
                });

                connection.port.on('close', function () {
                    node.status({fill:"red",shape:"ring",text:'Pixhawk/telemetry-port closed, trying to reconnect...'});
                    connection.devicepath = null;
                    if (connection.tout !== null) { clearTimeout(connection.tout); }
                    if (!connection.shutdown) {
            		    connection.tout = setTimeout(tryToOpenPort(null), settings.serialReconnectTime);
                    }
                    //port = undefined;
                });

        		//Parse any new incoming data
        		connection.port.on('data', function(data) {
        		    if (connection.mavReady) {
            		    //Sending message
            		    try {
                			//Check for new message to send
                			var newmessage = flow.get("newmessage")||0;
                		    if (newmessage !== 0) {
                 		        connection.m.createMessage(newmessage.type, newmessage.fields, connection.writemessage);
                		    }
                			flow.set("newmessage",0);
            		    }
            		    catch (err) {
                            node.status({fill:"red",shape:"ring",text:err.message});
                        }

                        //Receiving message
                        try {
                			connection.m.parse(data);
                        }
                        catch (err) {
                            node.status({fill:"red",shape:"ring",text:err.message});
                        }
        		    }
            	});
             }
            catch (err) {
                node.status({fill:"red",shape:"dot",text:'Something went wrong. Trying again...'});
                let currerror = err.toString();
                if (currerror != connection.lasterror) {
                    connection.lasterror = err.toString();
                    console.log(connection.lasterror);
                }
                if (connection.tout !== null) { clearTimeout(connection.tout); }
                connection.tout = setTimeout(tryToOpenPort(null), settings.serialReconnectTime);
            }
        } else {
            node.status({fill:"red",shape:"ring",text:'No Pixhawk/telemetry-device found yet'});
            SerialPort.list()
                .then((founddevices) => {
                    //Choose right serial device
                    connection.devicepath = findPathToManufacturer(founddevices, "3D Robotics", "FTDI");
                    if (connection.tout !== null) { clearTimeout(connection.tout); }
                    connection.tout = setTimeout(tryToOpenPort(connection.devicepath), settings.serialReconnectTime);
                })
                .catch((err) => {
                    connection.devicepath = null;
                    console.log("An error occured when trying to get device-list:");
                    let currerror = err.toString();
                    if (currerror != connection.lasterror) {
                        connection.lasterror = err.toString();
                        console.log(connection.lasterror);
                    }
                    if (connection.tout !== null) { clearTimeout(connection.tout); }
                    connection.tout = setTimeout(tryToOpenPort(null), settings.serialReconnectTime);
                });
        }
    }
}

//------------------------------------------
//      Startup
//------------------------------------------

node.status({fill:"yellow",shape:"ring",text:"Search for serial devices and starting MAVlink..."});

//------------Get MAVlink ready------------

var MAV_SYS_ID = env.get("MAV_SYS_ID")||1;
var MAV_COMP_ID = env.get("MAV_COMP_ID")||1;

//listening for system 1 component 1
connection.m = new mavlink(MAV_SYS_ID,MAV_COMP_ID);

//When mavlink is ready, assign some listeners
connection.m.on('ready', function() {
    console.log("Mavlink is ready!");
    node.status({fill:"green",shape:"ring",text:'Mavlink is ready'});
    connection.mavReady = true;

		//Attitude listener
		connection.m.on('ATTITUDE', function(message, fields) {
			//Do something interesting with Attitude data here
			msg = {};
			msg.payload = fields;
			msg.topic = 'ATTITUDE';
			node.send(msg);
			//console.log("Roll is " + fields.roll + "\nPitch is " + fields.pitch);
		});

		//GLOBAL_POSITION_INT listener
