			//Do something interesting with Attitude data here
			msg = {};
			msg.payload = fields;
			msg.topic = 'ALTITUDE';
			node.send(msg);
			//console.log("Roll is " + fields.roll + "\nPitch is " + fields.pitch);
		});

		//VIBRATION listener
		connection.m.on('VIBRATION', function(message, fields) {
			//Do something interesting with Attitude data here
			msg = {};
			msg.payload = fields;
			msg.topic = 'VIBRATION';
			node.send(msg);
			//console.log("Roll is " + fields.roll + "\nPitch is " + fields.pitch);
		});

		//NAMED_VALUE_FLOAT listener
		connection.m.on('NAMED_VALUE_FLOAT', function(message, fields) {
			//Do something interesting with Attitude data here
			msg = {};
			msg.payload = fields;
			msg.topic = 'NAMED_VALUE_FLOAT';
			node.send(msg);
			//console.log("Roll is " + fields.roll + "\nPitch is " + fields.pitch);
		});
});

//------------Get correct Serial-port-------

//Find serial devices and (repeatedly) try to open port
if (connection.tout !== null) { clearTimeout(connection.tout); }
connection.tout = setTimeout(tryToOpenPort(null), settings.serialReconnectTime);

node.on('close',function() {
    console.log("Pixhawk/telemetry-port closing...");
    connection.shutdown = true;
        try {
            connection.port.close(function(err) {
                if (err !== null) {
                    node.log('Pixhawk/telemetry-port closed with error');
                    node.log(err);
                    //console.log('port closed', err);
                } else {
                    node.status({fill:"red",shape:"ring",text:'Pixhawk/telemetry-port closed'});
                }
            });
        }
        catch (err) {
                if (err !== null) {
                    node.log('Pixhawk/telemetry-port closed with error');
                    node.log(err);
                    //console.log('port closed', err);
                } else {
                    node.status({fill:"red",shape:"ring",text:'Pixhawk/telemetry-port closed'});
                }
         }
        if (connection.tout !== null) { clearTimeout(connection.tout); }
})

if (typeof msg.topic == "string" && typeof msg.payload == "number") {
    newmessage = {};
    newmessage.type = "NAMED_VALUE_FLOAT";
    newmessage.fields = {
    			'time_boot_ms':	Date.now()%1E9,
    			'name':	        msg.topic,
    			'value':	    msg.payload
    			};
    flow.set("newmessage",newmessage);
} else {
    node.status({fill:"yellow",shape:"ring",text:'Message rejected, wrong format'});
}
